import SentenceItem from "./Items/SentenceItem";

const Sentence = (props) => {
  let modifyText = (text) => {
    text = text.split(".", 1);
    return text + ".";
  };
  return (
    <div>
      {props.data.map((el, index) => {
        return (
          <div>
            <SentenceItem
              key={index}
              data={modifyText(el)}
              updateData={props.updateData}
              index={index}
            />
            <button>1231234</button>
          </div>
        );
      })}
    </div>
  );
};

export default Sentence;
