import logo from "./logo.svg";
import "./App.css";
/* import { BrowserRouter, Routes, Route, Link } from "react-router-dom"; */
import React, { useState, useEffect } from "react";
import Nav from "./Action/Nav";
import CutData from "./Components/CutData";
import Data from "./Components/Data";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

function App() {
  const api = "https://baconipsum.com/api/?type=meat-andfiller";
  let [data, SetData] = useState([]);
  let [CutData, SetCutData] = useState([]);

  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((result) => {
        SetData(result);
      });
  }, []);

  let modifyText = (text) => {
    text = text.split(".", 1);
    return text + ".";
  };

  let curIndex = null;
  let updateData = (value) => {
    SetCutData(value)
  };

  return (
    <div className="App">
      <header className="App-header">
        <BrowserRouter>
          <Routes>
            <Route
              path="/"
              element={data.map((el, index) => {
                return (
                  <CutData
                    CutData={modifyText(el)}
                    index={index}
                    updateData={updateData}
                  />
                );
              })}
            />
            <Route
              path="/src/components/Data.js"
              element={data.map((el) => {
                if (curIndex != null) {
                  return <Data Data={el} curIndex={CutData} />;
                } else <p>loading messages</p>;
              })}
            />
          </Routes>
        </BrowserRouter>
      </header>
    </div>
  );
}

export default App;
