import logo from "./logo.svg";
import "./App.css";
/* import { BrowserRouter, Routes, Route, Link } from "react-router-dom"; */
import React, { useState, useEffect } from "react";
import Nav from "./Action/Nav";
import CutData from "./Components/CutData";
import Data from "./Components/Data";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

function App() {
  const api = "https://baconipsum.com/api/?type=meat-andfiller";
  let [data, SetData] = useState([]);

  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((result) => {
        SetData(result);
      });
  }, []);

  let modifyText = (text) => {
    text = text.split(".", 1);
    return text + ".";
  };

  let curIndex;
  let updateData = (value) => {
    curIndex = value;
  };
  return (
    <div className="App">
      <header className="App-header">
        <BrowserRouter>
          <Routes>
            <Route
              path="/"
              element={data.map((el, index) => {
                return (
                  <CutData
                    CutData={modifyText(el)}
                    index={index}
                    updateData={updateData}
                  />
                );
              })}
            />
            <Route
              path="/src/components/Data.js"
              element={
                <Data data={data} curIndex={curIndex} />
              }
            />
            {/* <Route
              path="/src/components/Data.js"
              element={data.map((el) => {
                return <Data data={el} curIndex={updateData} />;
              })}
            /> */}
          </Routes>
        </BrowserRouter>
      </header>
    </div>
  );
}

export default App;
