import React from "react";
import CutData from "./CutData";
import Data from "./Data";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import s from "./../Styles/App.module.css";

class AppMain extends React.Component {
  state = {
    index: 0,
    data: [],
    api: "https://baconipsum.com/api/?type=meat-andfiller",
  };

  updateData = (value) => {
    this.setState({ index: value });
  };

  componentDidMount() {
    fetch(this.state.api)
      .then((res) => res.json())
      .then((result) => {
        this.setState({ data: result });
      });
  }

  render() {
    let modifyText = (text) => {
      text = text.split(".", 1);
      return text + ".";
    };
    return (
      <div className={s.App}>
        <div>
          <BrowserRouter>
            <Routes>
              <Route
                path="/"
                element={this.state.data.map((el, index) => {
                    <div>
                      <CutData
                        key={index}
                        CutData={modifyText(el)}
                        index={index}
                        updateData={this.updateData}
                      />
                      <div>123</div>
                    </div>
                })}
              />
              <Route
                path="/src/components/Data.js"
                element={
                  <Data data={this.state.data} index={this.state.index} />
                }
              />
            </Routes>
          </BrowserRouter>
        </div>
      </div>
    );
  }
}

export default AppMain;
