import logo from "./logo.svg";
import "./App.css";
/* import { BrowserRouter, Routes, Route, Link } from "react-router-dom"; */
import React, { useState, useEffect } from "react";
import Nav from "./Action/Nav";
import CutData from "./Components/CutData";
import Data from "./Components/Data";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

function App() {
  const api = "https://baconipsum.com/api/?type=meat-andfiller";
  let [data, SetData] = useState([]);
  let [data1, SetData1] = useState([]);

  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((result) => {
        SetData(result);
      });
  }, []);

  let modifyText = (text) => {
    text = text.split(".", 1);
    return text + ".";
  };
  return (
    <div className="App">
      <header className="App-header">
        <BrowserRouter>
          <Routes>
            <Route
              path="/"
              element={data.map((el,index) => {
                return <CutData CutData={modifyText(el)} />;
              })}
            />
            <Route
              path="/src/components/Data.js"
              element={data.map((el,index) => {
                return <Data Data={el} index={index} />;
              })}
            />
          </Routes>
        </BrowserRouter>
      </header>
    </div>
  );
}

export default App;
