import { render } from "react-dom";
import logo from "./logo.svg";
import "./App.css";
import React, { useState, useEffect } from "react";
import CutData from "./Components/CutData";
import Data from "./Components/Data";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

class AppMain extends React.Component {
  state = {
    index: 0,
    data: [],
    api: "https://baconipsum.com/api/?type=meat-andfiller",
  };

  updateData = (value) => {
      this.setState({ index: value })
  };

  componentDidMount() {
    fetch(this.state.api)
      .then((res) => res.json())
      .then((result) => {
        this.setState({ data: result });
      });
  }

  render() {
    let modifyText = (text) => {
      text = text.split(".", 1);
      return text + ".";
    };
    return (
      <div className="App">
        <header className="App-header">
          <BrowserRouter>
            <Routes>
              <Route
                path="/"
                element={this.state.data.map((el, index) => {
                  return (
                    <CutData
                      CutData={modifyText(el)}
                      index={index}
                      updateData={this.updateData}
                    />
                  );
                })}
              />
              <Route
                path="/src/components/Data.js"
                element={
                  <Data data={this.state.data} index={this.state.index} />
                }
              />
            </Routes>
          </BrowserRouter>
        </header>
      </div>
    );
  }
}

export default AppMain;
