import logo from "./logo.svg";
import "./App.css";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import React, { useState, useEffect } from "react";
import Nav from "./Action/Nav";
import CutData from "./Components/CutData";
import Data from "./Components/Data";

function App() {
  const api = "https://baconipsum.com/api/?type=meat-andfiller";
  let [data, SetData] = useState([]);

  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((result) => {
        SetData(result);
      });
  }, []);

  let modifyText = (text) => {
    text = text.split(".", 1);
    return text + ".";
  };
  return (
    <div className="App">
      <header className="App-header">
        <BrowserRouter>
        {data.map((el) => {
        return <Nav CutData={modifyText(el)}/>
      })}
          <Nav />
          <Routes>
            {/* <Route path="/src/components/CutData.js" element={<CutData />} /> */}
            <Route path="/src/components/Data.js" element={<Data />} />
          </Routes>
        </BrowserRouter>
      </header>
    </div>
  );
}

export default App;
