import { Link } from "react-router-dom";
import s from "../../Styles/App.module.css"

const SentenceItem = (props) => {
  let modifyText = (text) => {
    text = text.split(".", 1);
    return text + ".";
  };
  return (
    <ul className={s.Articles}>
      {/* <NavLink to="/src/components/Data.js">
        <li
          onClick={() => {
            props.updateData(props.index);
          }}
        >
          <a>{props.CutData}</a>
        </li>
      </NavLink> */}
      <Link to="/Articles.js">
        <li>
          <a>{modifyText(props.data)}</a>
        </li>
      </Link>
    </ul>
  );
};

export default SentenceItem;
